#include <iostream>#include <vector>using std::vector;using std::cout;using std::endl;int main(){	vector<int> num_visitors = {50, 10, 14, 7, 25, 30, 70};	cout << "星期日： " << num_visitors.at(0) << " 人" << endl;	cout << "星期一： " << num_visitors.at(1) << " 人" << endl;	cout << "星期二： " << num_visitors.at(2) << " 人" << endl;	cout << "星期三： " << num_visitors.at(3) << " 人" << endl;	cout << "星期四： " << num_visitors.at(4) << " 人" << endl;	cout << "星期五： " << num_visitors.at(5) << " 人" << endl;	cout << "星期六： " << num_visitors.at(6) << " 人" << endl;}