#include <iostream>#include <vector>using std::vector;using std::ostream;using std::cout;using std::endl;ostream &operator<<(ostream &os, vector<int> data){	os << '[';	for(auto &d: data){		os << d << (&d==&data.back()? "":", ");}	return os << ']';}void swap(vector<int> &data, int x, int y){	int temp = data.at(x);	data.at(x) = data.at(y);	data.at(y) = temp;}void one_bubble(vector<int> &data, int end_index){	for(int j=0; j<end_index;++j){		if(data.at(j) > data.at(j+1)){			swap(data, j, j+1);}}}int main(){	vector<int> data = {4, 1, 3, 5, 2};	int data_count = data.size();	for(int i=0; i<data_count-1; ++i){		one_bubble(data, data_count-1-i);		cout << "第 " << i+1 << " 回合結果: " << data << endl;	}	cout << "排序結束" << endl;}