#include <iostream>#include <vector>using std::vector;using std::ostream;using std::cout;using std::endl;ostream &operator<<(ostream &os, vector<int> data){	os << '[';	for(auto &d: data){		os << d << (&d==&data.back()? "":", ");}	return os << ']';}int seq_search(vector<int> &data, int data_count, int target){	for(int i=0; i<data_count; ++i){		if(data.at(i) == target){			return i;}}	return -1;}int main(){	vector<int> data = {12, 2, 7, 5, 9};	int data_count = data.size();	int target = 7;	int found_index = seq_search(data, data_count, target);	if(found_index == -1){		cout<<"無法找到搜尋目標 " << target << endl;}	else{		cout << "發現搜尋目標 " << target << " , 位於陣列中第 " << found_index << " 個位置" << endl;}}